// PLUGINS IMPORTS //
import Image from "next/image"
import styled from "styled-components"

// COMPONENTS IMPORTS //
import Heading from "components/typography/hero-heading"
import Button from "components/atoms/button"

// EXTRA IMPORTS //
import styles from "./header.module.scss"

/////////////////////////////////////////////////////////////////////////////

const Header = () => {
  return (
    <Wrapper>
      <div className={styles.image_wrap}>
        <Image
          src={"/images/logo-white.png"}
          height={35}
          width={70}
          alt="Logo"
          layout="intrinsic"
        />
      </div>

      <div className={styles.content}>
        <Heading title={"Outdoors"} subtitle={"is where life happens"} />
        <Button text={"Discover out tours"} />
      </div>
    </Wrapper>
  )
}

/////////////////////////////////////////////////////////////////////////////

const TRANSPARENCY = 0.8
const Wrapper = styled.header`
  height: 95vh;
  background-image:  linear-gradient(
      to right,
      rgba(#7ed56f, TRANSPARENCY),
      rgba(${props.theme.primaryDark}, TRANSPARENCY)
    )
  background-size: cover;
  background-position: top;
  clip-path: polygon(0 0, 100% 0, 100% 75vh, 0 100%);
  position: relative;
`

export default Header
